import React from 'react';
const UploadForm = () => {
    const [file, setFile] = useState(null);

    const changeHandler = (e) => {
        console.log('changed');
        let selected = e.target.files[0];
        console.log(selected)
        // Do we have a File
        if(selected){
            setFile(selected);
        }
    }

    return(
        <form>
            <input type="file" onChange = {changeHandler}/>
        </form>
    )
}
export default UploadForm;
